import React, {Component} from 'react';
import axios from 'axios'; 
import Navbar from './Navbar';

class SinglePost extends Component{

	constructor(props){
		super(props);

		this.state = {
			loading:false,
			post:{},
			error: ''
		}
	}

	componentDidMount(){
		console.log(this.props.id);
		const wordPressSiteUrl = 'https://www.futuremedicineindia.com/';
		this.setState({loading:true}, () =>{
			axios.get(`${wordPressSiteUrl}/wp-json/wp/v2/posts/${this.props.id}?_embed`)
				.then(res =>{
					// console.log(res.data);
					this.setState({
						loading: false,
						post: res.data 
					});
				})
				.catch( error => this.setState({loading:false,error:error.response.data.message}))
		})
	}


	render(){
		return(
			<div>
				<Navbar/>
				{error && <div className="error-allert mt-3">{error}</div>}
				
				{Object.keys(post) ? (
					<div className="mt-5 post-container container">
						<div className="row">
						{posts.map(post =>(
							<div className="col col-12 col-md-6 col-lg-4">
								<div key={post.id} className="card border-dark mb-3">
									<div className="card-header">
										<Link to={`/post/${post.id}`}>{post.title.rendered}</Link>
									</div>
									<div className="card-body">
										<div className="imgbox pb-3">
											<img className="w-100" src={post._embedded['wp:featuredmedia']['0'].source_url}/>
										</div>
										<div className="card-text post-content">
											{renderHTML(post.excerpt.rendered)}
										</div>
										<div className="card-footer">
											<Moment fromNow>{post.date}</Moment>
											<Link top={`/post/${post.id}`} className="btn btn-secondary float-right">Read More..</Link>	
										</div>
									</div>
								</div>
							</div>
						))}
						</div>
					</div>	
				) : '' }

				<div className="container">
					{loading && <img className="loader" src={Loader}/>}
				</div>


			</div>
		)
	}
}

export default SinglePost;
