import React, {Component} from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
import renderHTML from 'react-render-html';
import Moment from 'react-moment';

import Navbar from './Navbar';
import axios from 'axios'; 

class Home extends Component {
	
	constructor(props){
		super(props);

		this.state = {
			loading:false,
			posts: [],
			error: ''
		}
	}

	componentDidMount(){
		const wordPressSiteUrl = 'https://www.futuremedicineindia.com/';
		this.setState({loading:true}, () =>{
			axios.get(`${wordPressSiteUrl}/wp-json/wp/v2/posts?categories=7`)
				.then(res =>{
					// console.log(res.data);
					this.setState({
						loading: false,
						posts: res.data 
					});
				})
				.catch( error => this.setState({loading:false,error:error.response.data}))
		})
	}
	
	render(){
		console.log('state', this.state);
		const {posts} = this.state;
		// const posts = this.state.posts;

		return(
			<div className="">
				<Navbar/>
				{posts.length ? (
					<div className="mt-5 post-container container">
						<div className="row">
						{posts.map(post =>(
							<div className="col col-12 col-md-6 col-lg-4">
								<div key={post.id} className="card border-dark mb-3">
									<div className="card-header">
										<Link to={`/post/${post.id}`}>{post.title.rendered}</Link>
									</div>
									<div className="card-body">
										<div className="imgbox pb-3">
											<img className="w-100" src={post.fimg_url}/>
										</div>
										<div className="card-text post-content">
											{renderHTML(post.excerpt.rendered)}
										</div>
										<div className="card-footer">
											<Moment fromNow>{post.date}</Moment>
										</div>

									</div>
								</div>
							</div>
						))}
						</div>
					</div>	
				) : '' }
			</div>
		)
	}
}

export default Home;
