import React, {Component} from 'react';
import Navbar from './Navbar';
import axios from 'axios'; 
import { BrowserRouter, Route, Link } from "react-router-dom";


class Home extends Component {
	
	constructor(props){
		super(props);

		this.state = {
			loading:false,
			posts: [],
			error: ''
		}
	}

	componentDidMount(){
		const wordPressSiteUrl = 'http://sdblogistics.com/sdblogisticsdemo/';
		this.setState({loading:true}, () =>{
			axios.get(`${wordPressSiteUrl}/wp-json/wp/v2/posts?categories=7`)
				.then(res =>{
					// console.log(res.data);
					this.setState({
						loading: false,
						posts: res.data 
					});
				})
				.catch( error => this.setState({loading:false,error:error.response.data}))
		})
	}
	
	render(){
		console.log('state', this.state);
		const {posts} = this.state;
		// const posts = this.state.posts;

		return(
			<div className="">
				<Navbar/>
				{posts.length ? (
					<div className="mt-5 post-container">
						{posts.map(post =>(
							<div key={post.id} className="card border-dark mb-3">
								<h2>{post.title.rendered}</h2>
								<Link to={`/post/${post.id}`}></Link>
							</div>
						))}
					</div>	
				) : '' }
			</div>
		)
	}
}

export default Home;
