import React, {Component} from 'react';
import Navbar from './Navbar';
import axios from 'axios'; 


class Home extends Component {
	
	constructor(props){
		super(props);

		this.state = {
			loading:false,
			posts: [],
			error: ''
		}
	}

	componentDidMount(){
		const wordPressSiteUrl = 'http://sdblogistics.com/sdblogisticsdemo/';
		this.setState({loading:true}, () =>{
			axios.get(`${wordPressSiteUrl}/wp-json/wp/v2/posts?categories=7`)
				.then(res =>{
					console.log(res.data.id);
				})
				.catch( error => this.setState({loading:false,error:error.response.data}))
		})
	}
	
	render(){
		return(
			<div className="">
				<Navbar/>
			</div>
		)
	}
}

export default Home;
